import React, { useEffect, useRef } from "react";
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';
import Button from "../components/Button/Button";
import Input from "../components/Input/Input";
import { useTemplatesStore } from "../store/templates.mjs";
import FiveModal from "../components/Modals/FiveModal";
import WheelModal from "../components/Modals/WheelModal";
import PestleModal from "../components/Modals/PestleModal";

function Dashboard() {
  const { templates, changeTemplate } = useTemplatesStore();
  const [selectedTemplate, setSelectedTemplate] = React.useState(
    templates[0].name
  );
  const [values, setValues] = React.useState({});
  const [openFiveModal, setOpenFiveModal] = React.useState({
    five_w: false,
    wheel_of_reasoning: false,
    pestle: false,
  });
  const template = templates.find((t) => t.name === selectedTemplate);

  const onSelect = (e) => {
    setSelectedTemplate(e.target.value);
  };
  const onChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  const save = () => {
    const saveTemplate = {
      ...template,
      list: template.list.map((item) => ({
        ...item,
        answer: values[item.id],
      })),
    };
    changeTemplate(saveTemplate);
  };

  useEffect(() => {
    const initial = templates[0]?.list.reduce((acc, item) => {
      acc[item.id] = item.answer;
      return acc;
    }, {});
    setValues(initial);
  }, []);

  const toggleModal = () => {
    setOpenFiveModal((prev) => ({
      ...prev,
      [template.name]: !openFiveModal[template.name],
    }));
  };

  const formRef = useRef();
  const token = "YOUR_TOKEN_HERE"; // Вкажіть правильний токен

  const saveAsPDF = () => {
    const input = formRef.current;
    html2canvas(input)
      .then((canvas) => {
        const imgData = canvas.toDataURL('image/png');
        const pdf = new jsPDF();
        pdf.addImage(imgData, 'PNG', 0, 0);
        pdf.save("download.pdf");
      });
  };

  const saveAsPNG = () => {
    const input = formRef.current;
    html2canvas(input)
      .then((canvas) => {
        const imgData = canvas.toDataURL('image/png');
        const link = document.createElement('a');
        link.href = imgData;
        link.download = 'download.png';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
      });
  };

  const uploadFile = async (file) => {
    const formData = new FormData();
    formData.append('file', file);

    const response = await fetch("http://127.0.0.1:8000/api/upload-template/", {
      method: "POST",
      headers: {
        "Authorization": `Token ${token}`
      },
      body: formData
    });

    if (!response.ok) {
      throw new Error('Failed to upload file');
    }

    return response.json();
  };

  const saveAndUploadPDF = async () => {
    const input = formRef.current;
    html2canvas(input)
      .then((canvas) => {
        const imgData = canvas.toDataURL('image/png');
        const pdf = new jsPDF();
        pdf.addImage(imgData, 'PNG', 0, 0);
        const pdfBlob = pdf.output('blob');
        uploadFile(pdfBlob);
      });
  };

  return (
    <div className="flex flex-1 flex-col">
      <h1 className="text-4xl bg-cblue text-white font-bold p-2.5 text-center">
        {template?.title || "Select a template"}
      </h1>
      <div ref={formRef}>
        <table className="table-fixed bg-white">
          <thead>
            <tr>
              <th className="px-4 py-2">Key factors</th>
              <th className="px-4 py-2">Questions</th>
              <th className="px-4 py-2">Your thoughts</th>
            </tr>
          </thead>
          <tbody>
            {template?.list.map((item, index) => (
              <tr key={item.id}>
                <td className="border px-4 py-2 w-[200px]">
                  {index + 1}. {item.method}
                </td>
                <td className="border px-4 py-2 w-[600px]">{item.question}</td>
                <td className="border px-4 py-2 w-ful">
                  <Input
                    onChange={onChange}
                    name={item.id}
                    value={values[item.id]}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="flex w-full justify-between gap-6 mt-10">
        <Button onClick={toggleModal}>INFO</Button>
        <div className="flex justify-end gap-6 ">
          <select
            className="px-2 text-lg bg-gray-50 border border-gray-300 text-gray-900 rounded-lg focus:ring-blue-500 focus:border-blue-500"
            onChange={onSelect}
          >
            {templates.map((item) => (
              <option key={item.name} value={item.name}>
                {item.title}
              </option>
            ))}
          </select>
          <Button onClick={saveAsPDF}>Save as PDF</Button>
          <Button onClick={saveAsPNG}>Save as PNG</Button>
        </div>
      </div>
      <FiveModal open={openFiveModal.five_w} onClose={toggleModal} />
      <WheelModal
        open={openFiveModal.wheel_of_reasoning}
        onClose={toggleModal}
      />
      <PestleModal open={openFiveModal.pestle} onClose={toggleModal} />
    </div>
  );
}

export default Dashboard;
