import React, { useState } from "react";
import Button from "../components/Button/Button";
import Input from "../components/Input/Input";
import { useAnalysisStore } from "../store/analysis";
import nlp from 'compromise';

function Analysis() {
  const { data, changeTemplate } = useAnalysisStore();
  const [value, setValue] = useState("");
  const [result, setResult] = useState("");

  const onChange = (e) => {
    setValue(e.target.value);
  };

  const handlePosAnalysis = () => {
    const doc = nlp(value);
    const pos = doc.out('tags');
    setResult(JSON.stringify(pos, null, 2));
  };

  return (
    <div className="flex flex-1 flex-col">
      <h1 className="text-4xl bg-cblue text-white font-bold p-2.5 text-center">
        Analysis
      </h1>
      <div className="border-5 border-cblue bg-white min-h-[400px] p-4">
        <textarea
          value={value}
          onChange={onChange}
          className="w-full h-full p-4 border-2"
          placeholder="Enter your text..."
        />
      </div>
      <div className="mt-10 w-full justify-center gap-6 flex">
        <Button className="!p-2 text-base" onClick={handlePosAnalysis}>
          Part-of-Speech analysis
        </Button>
        <Button className="!p-2 text-base">NER analysis</Button>
        <Button className="!p-2 text-base">Sentiment analysis</Button>
      </div>
      {result && (
        <div className="mt-10 w-full p-4 border-2 bg-gray-100">
          <h2 className="text-2xl font-bold mb-2">Analysis Result</h2>
          <pre>{result}</pre>
        </div>
      )}
    </div>
  );
}

export default Analysis;
