import React, { useState } from "react";
import Button from "../components/Button/Button";
import nlp from 'compromise';
import posTagger from 'wink-pos-tagger';
import Sentiment from 'sentiment';

const sentiment = new Sentiment();
const pos = posTagger();

function Analysis() {
  const [value, setValue] = useState("");
  const [analysisResult, setAnalysisResult] = useState("");

  const handlePartOfSpeech = () => {
    const tokens = pos.tag(value);
    setAnalysisResult(JSON.stringify(tokens, null, 2));
  };

  const handleNER = () => {
    const doc = nlp(value);
    const entities = doc.topics().json();
    setAnalysisResult(JSON.stringify(entities, null, 2));
  };

  const handleSentiment = () => {
    const result = sentiment.analyze(value);
    setAnalysisResult(JSON.stringify(result, null, 2));
  };

  return (
    <div className="flex flex-1 flex-col">
      <h1 className="text-4xl bg-cblue text-white font-bold p-2.5 text-center">
        Analysis
      </h1>
      <div className="border-5 border-cblue bg-white min-h-[400px] p-4">
        <textarea
          value={value}
          onChange={(e) => setValue(e.target.value)}
          className="w-full h-full p-4 border-2"
          placeholder="Enter your text..."
        />
      </div>
      <div className="mt-10 w-full justify-center gap-6 flex">
        <Button className="!p-2 text-base" onClick={handlePartOfSpeech}>Part-of-Speech analysis</Button>
        <Button className="!p-2 text-base" onClick={handleNER}>NER analysis</Button>
        <Button className="!p-2 text-base" onClick={handleSentiment}>Sentiment analysis</Button>
      </div>
      {analysisResult && (
        <div className="mt-10 w-full p-4 border-2 border-cblue bg-white">
          <pre>{analysisResult}</pre>
        </div>
      )}
    </div>
  );
}

export default Analysis;
