import React, { useState, useRef, useEffect } from 'react';
import PropTypes from 'prop-types';

const ExpandingTextarea = ({ name, value, onChange, placeholder, className }) => {
  const textareaRef = useRef(null);
  const [textAreaHeight, setTextAreaHeight] = useState('auto');

  useEffect(() => {
    setTextAreaHeight(`${textareaRef.current.scrollHeight}px`);
  }, [value]);

  const handleChange = (event) => {
    setTextAreaHeight('auto');
    if (onChange) {
      onChange(event);
    }
  };

  return (
    <textarea
      ref={textareaRef}
      name={name}
      value={value}
      onChange={handleChange}
      placeholder={placeholder}
      className={`p-6 text-lg bg-gray-50 border border-gray-300 text-gray-900 rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full ${className}`}
      style={{ height: textAreaHeight }}
    />
  );
};

ExpandingTextarea.propTypes = {
  name: PropTypes.string.isRequired,
  value: PropTypes.string,
  onChange: PropTypes.func,
  placeholder: PropTypes.string,
  className: PropTypes.string,
};

ExpandingTextarea.defaultProps = {
  value: '',
  placeholder: '',
};

export default ExpandingTextarea;
