function Input({ name, type = "text", placeholder = "", onChange }) {
  return (
    <div>
      <input
        name={name}
        type={type}
        placeholder={placeholder}
        onChange={onChange}
        className={`p-6 text-lg bg-gray-50 border border-gray-300 text-gray-900 rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full input ${inputClasses}`}
      />
    </div>
  );
}

// Значення за замовчуванням для параметрів функції
Input.defaultProps = {
  type: "text",
  placeholder: "",
};
