import React, { useState } from 'react';
import PropTypes from 'prop-types';

const ExpandingInput = ({ name, value, onChange, placeholder, className }) => {
  const [inputHeight, setInputHeight] = useState('auto');

  const handleInputChange = (event) => {
    const { scrollHeight, clientHeight } = event.target;
    setInputHeight(`${Math.max(scrollHeight, clientHeight)}px`);

    if (onChange) {
      onChange(event);
    }
  };

  return (
    <input
      name={name}
      value={value}
      onChange={handleInputChange}
      placeholder={placeholder}
      className={`p-6 text-lg bg-gray-50 border border-gray-300 text-gray-900 rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full ${className}`}
      style={{ height: inputHeight }}
    />
  );
};

ExpandingInput.propTypes = {
  name: PropTypes.string.isRequired,
  value: PropTypes.string,
  onChange: PropTypes.func,
  placeholder: PropTypes.string,
  className: PropTypes.string,
};

ExpandingInput.defaultProps = {
  value: '',
  placeholder: '',
};

export default ExpandingInput;
