import React, { useEffect } from "react";
import Button from "../components/Button/Button";
import Input from "../components/Input/Input";
import { useAnalysisStore } from "../store/analysis";

function Analysis() {
  const { data, changeTemplate } = useAnalysisStore();
  const [value, setValue] = React.useState("");

  const onChange = (e) => {
    setValue(e.target.value);
  };

  return (
    <div className="flex flex-1 flex-col">
      <h1 className="text-4xl bg-cblue text-white font-bold p-2.5 text-center">
        Analysis
      </h1>
      <div className="border-5 border-cblue bg-white min-h-[400px] p-4">
        <textarea
          value={value}
          onChange={onChange}
          className="w-full h-full p-4 border-2"
          placeholder="Enter your text..."
        />
      </div>
      <div className="mt-10 w-full justify-center gap-6 flex">
        <Button className="!p-2 text-base">Part-of-Spech analysis</Button>
        <Button className="!p-2 text-base">NEAR analysis</Button>
        <Button className="!p-2 text-base">Sentiment analysis</Button>
      </div>
    </div>
  );
}

export default Analysis;
